mapper.update:
UPDATE approve
status = #{status}, approve_time = #{approveTime}, remark = #{remark},
WHERE id = #{id}

mapper.select:
SELECT a.*, u1.real_name as applicant_name, u2.real_name as approver_name, CASE WHEN a.type = 'borrow' THEN (SELECT d.title FROM document d JOIN borrow b ON d.id = b.document_id WHERE b.id = a.ref_id) WHEN a.type = 'void' THEN (SELECT title FROM document WHERE id = a.ref_id) ELSE 'Unknown' END as ref_title, CASE WHEN a.status = 0 THEN '待审批' WHEN a.status = 1 THEN '已通过' WHEN a.status = 2 THEN '已拒绝' ELSE '未知' END as status_text, a.apply_time as apply_time_str, a.approve_time as approve_time_str FROM approve a LEFT JOIN user u1 ON a.applicant_id = u1.id LEFT JOIN user u2 ON a.approver_id = u2.id
AND a.type = #{params.type}
AND a.status = #{params.status}
AND a.applicant_id = #{params.applicantId}
AND a.approver_id = #{params.approverId}
ORDER BY a.apply_time DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM approve a


        AND a.type = #{params.type}


        AND a.status = #{params.status}


        AND a.applicant_id = #{params.applicantId}


        AND a.approver_id = #{params.approverId}
